mapper.update:
UPDATE account
SET salt = #{salt}
WHERE uid = #{uid}

mapper.select:
SELECT EXISTS(
               SELECT uid
               FROM account
               WHERE email = #{email}
               LIMIT 1
           );

SELECT EXISTS(
               SELECT uid
               FROM account
               WHERE nickname = #{nickname}
               LIMIT 1
           );

SELECT uid
FROM account
WHERE email = #{email}

SELECT salt
FROM account
WHERE uid = #{uid}

SELECT uid, password, nickname, salt
FROM account
WHERE email = #{email}
